
public class Declaraciones_Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Variable constante 
		final double apulgadas = 2.54;
				
		double cm = 6;
				
		double result = cm/apulgadas; 
				
		System.out.println(
				"En " + cm + " cm hay "
				+ result + " pulgadas"
		);
	}

}
